$(function(){var i=$("#LoutiNav"),o=i.find("li"),t=$("#main .louceng"),a=$("#goTop");$(window).scroll(function(){var e=$(window).height(),n=$(window).scrollTop();n>=$("#main").offset().top?(i.fadeIn(),a.fadeIn(),t.each(function(){e+n-$(this).offset().top>e/2&&(o.removeClass("active"),o.eq($(this).index()).addClass("active"))})):(i.fadeOut(),a.fadeOut())}),o.click(function(){var i=t.eq($(this).index()).offset().top;$("body,html").animate({scrollTop:i},500),$(this).addClass("active").siblings().removeClass("active")})});